mapper.select:
SELECT

FROM permission p
WHERE subject_type = #{subjectType}
AND subject_id = #{subjectId}
AND todo_id = #{todoId}

SELECT

FROM permission p
WHERE p.subject_type = 2
AND p.subject_id = #{userId}
AND p.todo_id = #{todoId}
UNION
SELECT

FROM permission p
LEFT JOIN user_role ur on p.subject_id=ur.role_id
WHERE p.subject_type = 1
AND p.todo_id = #{todoId}
AND ur.user_id = #{userId}

mapper.delete:
DELETE FROM permission
WHERE subject_type = #{subjectType}
AND subject_id = #{subjectId}
AND todo_id = #{todoId}

DELETE FROM permission
WHERE subject_type = 1
AND subject_id = #{roleId}
