mapper.update:
UPDATE todo
SET name = #{name},
status = #{status},
due_date = #{dueDate},
description = #{description}
WHERE id = #{id}

mapper.select:
SELECT

FROM todo t
WHERE id = #{id}

SELECT

FROM (
SELECT DISTINCT todo_id
FROM permission
WHERE subject_type = 2
AND subject_id = #{query.userId}

UNION

SELECT DISTINCT p.todo_id
FROM permission p
JOIN user_role ur
ON p.subject_type = 1
AND p.subject_id = ur.role_id
WHERE ur.user_id = #{query.userId}
) AS u_perm
LEFT JOIN todo t ON t.id = u_perm.todo_id
WHERE 1 = 1

    AND name LIKE CONCAT('%', #{query.name}, '%')


    AND status = #{query.status}


    AND due_date >= #{query.dueDateFrom}


    AND due_date <= #{query.dueDateTo}


    ORDER BY ${query.orderBy}
